    public List<Model_danych_szukaj_slowa> PokazOdnosniki(String NrKsiegi, String NrRozdzialu, String NrWersetu) {
        openDatabase();
        String[] ksiega = {NrKsiegi, NrRozdzialu, NrWersetu};


        String mySQL = "SELECT Bible.Book,Bible.Chapter,Odnosniki.Numer_Odnosnika,Bible.Verse, Bible.Scripture AS Odnosnik FROM Bible INNER JOIN Odnosniki ON tresc_wersetu_rowid = Bible._rowid_  where Odnosniki.book=? and Odnosniki.chapter=? and Odnosniki.verse=?";


        List<Model_danych_szukaj_slowa> ListaWersetow = new ArrayList<>();

        Cursor k = mDatabase.rawQuery(mySQL, ksiega);

        k.moveToFirst();
        while (!k.isAfterLast()) {


            String NumerKsiegi = k.getString(0);
            String NumerRozdzialu = k.getString(1);
            String NumerOdnosnika = k.getString(2);
            String NumerWersetu = k.getString(3);
            String TrescWersetu = k.getString(4);
            Model_danych_szukaj_slowa model_danych_szukaj_slowa = new Model_danych_szukaj_slowa("<sup><small>"+NumerOdnosnika+"</small></sup>"+ZamienNumerKsiegiNaNazweKsiegi(NumerKsiegi),NumerRozdzialu + ":" + NumerWersetu + ":" + TrescWersetu);

            ListaWersetow.add(model_danych_szukaj_slowa);
            k.moveToNext();

        }

        k.close();
        closeDatabase();
        return ListaWersetow;

    }

